import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AddThread from './AddThread';
import Profile from '../Profile'

class ThreadList extends Component {
  constructor(props){
      super(props);
      this.state = { 
        listOfThreads: [], 
        listOfUsers: []
      };
  }

  getAllThreads = () =>{
    axios.get(`/api/threads`)
    .then(responseFromApi => {
      this.setState({
        listOfThreads: responseFromApi.data

      })
    })
  }

  getallUsers = () => {
    axios.get("/api/users").then(response => {
      this.setState({
        listOfUsers: response.data
      })
    })
  }

  componentDidMount() {
    this.getAllThreads();
    this.getAllThreads()
  }

  render(){
    console.log(this.state.listOfThreads)
    // console.log("threadList props", this.props)
    return(
      <>
        {/* <Navbar /> */}
          <div className="threads">
            <div className="threadlist">
              <div className="add-thread">
                <h2>Add a New Thread</h2>
                  <AddThread getData={() => this.getAllThreads()} user={this.props.user}/> {/* <== !!! */}
              </div>
              <div className="threadlist-results">
                { this.state.listOfThreads.map( thread => {
                  console.log("thread map:", thread)
                  return ( 
                    <div className="threadlist-individual-result" key={thread._id}>

                      <Link to={`/threads/${thread._id}`}>
                        <h3>{thread.title}</h3>
                      </Link>

                      <Link to={`/profile/${thread.user}`}>
                        <h5>{this.props.user.username?this.props.user.username:null}</h5>
                      </Link>

                      <p>{thread.description} </p>

                      <div className="threadlist-buttons">
                        <Link to={`/threads/${thread._id}`}>
                          <p>Go to Post</p>
                        </Link>
                        <Link to={`/threads/${thread._id}`}>
                          <p>Add to favourites</p>
                        </Link>

                      </div>
                    </div>
                  )})
                }
              </div>
            </div>
          </div>
      </>
    )
  }
}

export default ThreadList;
