import React, { useState, useEffect } from "react";
import ReactMapGL, { Marker, Popup } from "react-map-gl";

const Map = () => {
  const [viewport, setViewport] = useState({
    latitude: 52.5200,
    longitude: 13.4050,
    width: "50vw",
    height: "50vh",
    zoom: 12
  });

  console.log(viewport)

  return (

    <div>
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken="pk.eyJ1IjoicmFjaGVsZGx0IiwiYSI6ImNrYzdsMG9qZTBxOGMyc2xqMzV2ejd1czEifQ.4f9dZK4w0vGTCrStvdKzlQ"
        mapStyle='mapbox://styles/mapbox/streets-v11'
        onViewportChange={viewport => {
          setViewport(viewport);
        }}
      >
      </ReactMapGL>
    </div>
  );}

  class AddressList extends Component {
    constructor(){
        super();
        this.state = { listOfAddress: [] };
    }

    getAllAddress = () =>{
      axios.get(`/api/user.address`)
      .then(responseFromApi => {
        this.setState({
          listOfThreads: responseFromApi.data
        })
      })
    }

    componentDidMount() {
      this.getAllThreads();
    }

    render(){
      return(
        <div className="threadlist">
           <div className="add-thread">
            <h2>Add a New Thread</h2>
              <AddThread getData={() => this.getAllThreads()}/> {/* <== !!! */}
          </div>
          <div className="threadlist-results">
            { this.state.listOfThreads.map( thread => {
              return ( 

                <div className="threadlist-individual-result" key={thread._id}>

                  <Link to={`/threads/${thread._id}`}>
                    <h3>{thread.title}</h3>
                  </Link>
                  <p>{thread.description} </p>

                  <div className="threadlist-buttons">
                  <Link to={`/threads/${thread._id}`}>
                    <p>Go to Post</p>
                  </Link>
                  <Link to={`/threads/${thread._id}`}>
                    <p>Add to favourites</p>
                  </Link>
                  </div>

                </div>

              )})
            }
          </div>

        </div>
      )
    }
  }

export default Map;
