import React, { Component } from 'react'; 
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import { Switch, Route, BrowserRouter,Redirect } from 'react-router-dom';
import Home from './components/Home.js';
import Profile from './components/Profile';
import Navbar from './components/Navbar'
import AddUser from './components/users/AddUser';
import UserList from './components/users/UserList';
import UserDetails from './components/users/UserDetails'
import Welcome from './components/Welcome'
import Signup from './components/Signup'
import Login from './components/Login';

import AddThread from './components/threads/AddThread';
import ThreadList from './components/threads/ThreadList';
import ThreadDetails from './components/threads/ThreadDetails'




class App extends React.Component {

  state = {
    user: this.props.user
  }
    setUser = user => {
    this.setState({
      user: user
    })
  }

  render() {
  return(
    <div className="app">
        <BrowserRouter>
          {/* <Switch> */}
          <Route exact path="/" component={Welcome}/>
            <Navbar /> 
            <Route exact path="/Home" render={() => <Home />} />
            {/* <Route exact path="/profile" render={() => this.state.user ? <Profile /> : <Redirect to='/' />}/> */}
            <Route exact path="/users" component={UserList}/>
            <Route exact path="/profile" component={Profile}/>
            <Route exact path="/users/:id" component={UserDetails} />
            <Route exact path="/threads" component={ThreadList}/>
            <Route exact path="/threads/:id" component={ThreadDetails} />
            <Route exact path="/Signup" render={props => <Signup setUser={this.setUser} {...props} />} />
          {/* // to the Signup we have to pass a reference to the setUser method
          // this we cannot do via component={<some component>}
          // For this we use the render prop - The term “render prop” refers to a technique for sharing 
          // code between React components using a prop whose value is a function.
          // A component with a render prop takes a function that returns a React element and calls it 
          // instead of implementing its own render logic. */}
            <Route exact path='/Login' render={(props) => <Login setUser={this.setUser} {...props}/>} />

          {/* </Switch> */}

        </BrowserRouter> 
    </div>
  );
}
}


export default App;
