import React, { useState, useEffect, Component } from "react";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import axios from 'axios';

const convertAddress=(address)=>{
 let url=`https://api.mapbox.com/geocoding/v5/mapbox.places/${address}.json?types=address&    access_token=pk.eyJ1IjoicmFjaGVsZGx0IiwiYSI6ImNrYzdsMG9qZTBxOGMyc2xqMzV2ejd1czEifQ.4f9dZK4w0vGTCrStvdKzlQ`
    return axios.get(url).then(response => {
      return response.data.features[0].center
 })

}
const Map = () => {

 const [address, setAddress] = useState({})
  const [viewport, setViewport] = useState({
    latitude: 52.5200,
    longitude: 13.4050,
    width: "50vw",
    height: "50vh",
    zoom: 12
  });

  let addressInfo = []

  // let coordinates = addressInfo.map(elem => {
  //   return convertAddress(elem)
  // })


  const getAllAddress = () =>{
    axios.get(`/api/users`)
    .then(responseFromApi => {
        console.log(responseFromApi)
        setAddress(responseFromApi)
    })
  }

  useEffect(() => { 
      getAllAddress()
  }, [])

  useEffect(() => { 
    let info = address.data

    if (info) {
        info.forEach(specificAddress => {
        addressInfo.push(specificAddress.address)
        })
    }

  }, [address])

  return (    
    <div>
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken="pk.eyJ1IjoicmFjaGVsZGx0IiwiYSI6ImNrYzdsMG9qZTBxOGMyc2xqMzV2ejd1czEifQ.4f9dZK4w0vGTCrStvdKzlQ"
        mapStyle='mapbox://styles/mapbox/streets-v11'
        onViewportChange={viewport => {
          setViewport(viewport);
        }}
      >
      </ReactMapGL>

      {/* <Marker
            //key={coordinates}
            latitude={coordinates[1]}
            longitude={coordinates[0]}
      >
      </Marker> */}
    </div>
  );}


export default Map;
